  warehouse_info = #{warehouseInfo,jdbcType=VARCHAR},
  company_id = #{companyId,jdbcType=VARCHAR},
  warehouse_last_time = #{warehouseLastTime,jdbcType=TIMESTAMP}
where warehouse_number = #{warehouseNumber,jdbcType=VARCHAR}
